declare module "*.png" { const value: any; export = value; }
declare module "*.svg" { const value: any; export = value; }

declare module "*.webp" {
    const value: any; export = value;
}

declare module '*.ttf' { const value: string; export = value; }
declare module '*.otf' { const value: string; export = value; }
declare module '*.woff' { const value: string; export = value; }
declare module '*.woff2' { const value: string; export = value; }

